import React from 'react';

import '../../stylesheets/forms.css';

const Input= ({
  id,
  type,
  value,
  isFocused,
  onFieldChange,
  children,
}) => {
  return (
    <>
      <label htmlFor={id}>{children}</label>
      <input
        className = 'fields'
        id={id}
        type={type}
        size={100}
        maxLength={100}
        value={value}
        autoFocus={isFocused}
        onChange={onFieldChange}
      /> 
      <br />
    </>
  );
}

export default Input;
