import React from 'react';

const TextArea = ({
  id,
  data,
  value,
  isFocused,
  onFieldChange,
  children,
}) => {
  return (
    <>
      <label htmlFor={id}>{children}</label><br />
      <textarea
        id={id}
        rows={3}
        cols={50}
        maxLength={100}
        value={value}
        autoFocus={isFocused}
        onChange={onFieldChange}
      > 
      </textarea>
    </> 
  );
}

export default TextArea;
