import React from 'react';

const TextArea = ({
  id,
  label,
  value,
  onChange,
}) => {
  return (
    <>
      <label htmlFor={id}>{label}</label><br />
      <textarea id={id} value={value} rows={3} cols={50} maxLength={100} onChange={onChange}> </textarea>
    </> 
  );
}

export default TextArea;
