import React from 'react';

let textArea = '';

const handleChange = (event) => {
  textArea = event.target.value;
};

const TextArea = () => {

  const [textArea, setTextArea] = React.useState('');

  return (
    <>
      <label htmlFor="vision">Your Vision:</label><br />
      <textarea id="vision" rows={3} cols={50} maxLength={100} autoFocus onChange={handleChange}> </textarea>
      <p>{textArea}</p>
    </> 
  );
}

export default TextArea;
