import * as React from 'react';

import Steps from '../steps/';

const App = () => {
  const myLifeCompass = {
    my_vision: {
      vision:'Test1'
    },
    my_purpose: {
      reason1: '',
      reason2: '',
      reason3: ''
    },
    my_values: {
      value1: '',
      value2: '',
      value3: '',
      value4: '',
      value5: ''
    }
  }

  const [textArea, setTextArea] = React.useState('');

  const [data, setData] = React.useState(myLifeCompass);

  const handleChange = (item) => {
    const newData = {item, ...data};

    setData(newData);
  };

  return (
    <div className="App">
      <div className="App-header">
        <Steps data={myLifeCompass} onChange={handleChange}/>
      </div>
      <p>{textArea}</p>
    </div>
  );
}

export default App;
